import { FILTER, ORDER_BY_NAME, ORDER_BY_RATING , LOAD_VIDEOGAMES, LOAD_GENRES } from "./actions/types";

const initialState = {
  allVideogames: [],
  allGenres: [],
};

const rootReducer = (state = initialState, { type, payload }) => {
  switch (type) {

    case LOAD_VIDEOGAMES: {
      return {
        ...state,
        allVideogames: payload,
      };
    }
    case LOAD_GENRES: {
      return {
        ...state,
        allGenres: payload,
      };
    }

    case ORDER_BY_NAME: 
    let videogames = state.allVideogames;
      const orderN = videogames.sort((a, b) => {
        if (payload === "A") {
          return a.name - b.name;
        } else if (payload === "D") {
          return b.name - a.name;
        } else {
          return 0;
        }
      });
      return {
        ...state,
        allVideogames: orderN,
      };

      case ORDER_BY_RATING: 
      const orderR = videogames.sort((a, b) => {
        if (payload === "A") {
          return a.rating - b.rating;
        } else if (payload === "D") {
          return b.rating - a.rating;
        } else {
          return 0;
        }
      });
      return {
        ...state,
        allVideogames: orderR,
      };

    default:
      return {
        ...state,
      };
  }
};

export default rootReducer;
