import { useDispatch, useSelector } from "react-redux";
import styles from "./form.module.css";
import { useState, useEffect } from "react";
import { loadGenres, createVideogame } from "../../redux/actions/actions";
import { useNavigate } from 'react-router-dom';

export default function Form() {
  const genres = useSelector((state) => state.allGenres);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { divPrinc, img, input, label, submit, checkbox, genreList, date } = styles;

  const [formData, setFormData] = useState({
    name: "",
    background_image: "",
    slug: "",
    platforms: [],
    released: "",
    rating: 0,
    genres: [],
  });

  const platforms = ["PC", "PlayStation 5", "PlayStation 4", "Xbox One", "Xbox Series S/X", "Nintendo Switch", "iOS", "Android", "Nintendo 3DS", "Nintendo DS", "Nintendo DSi", "macOS", "Linux", "Xbox 360", "Xbox", "PlayStation 3", "PlayStation 2", "PlayStation", "PS Vita", "PSP", "Wii U", "Wii", "GameCube", "Nintendo 64", "Game Boy Advance", "Game Boy Color", "Game Boy", "SNES", "NES", "Classic Macintosh", "Apple II", "Commodore / Amiga", "Atari 7800", "Atari 5200", "Atari 2600", "Atari Flashback", "Atari 8-bit", "Atari ST", "Atari Lynx", "Atari XEGS", "Genesis", "SEGA Saturn", "SEGA CD", "SEGA 32X", "SEGA Master System", "Dreamcast", "3DO", "Jaguar", "Game Gear", "Neo Geo"];

  useEffect(() => {
    dispatch(loadGenres()); 
  }, [dispatch]);

  const handleInputChange = (e) => {
    const name = e.target.name;
    const value = e.target.value;
  
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleGenre = (event) => {
    if (event.target.checked) {
      setFormData({ ...formData, genres: [...formData.genres, event.target.value] });
    } else {
      setFormData({ ...formData, genres: formData.genres.filter((gen) => gen.name !== event.target.value)});
    }   
};

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(createVideogame(formData));
  };

  return (
    <div className={divPrinc}>
      <form onSubmit={handleSubmit}>
        <img
          className={img}
          src="https://pandaancha.mx/plds/articulos/froala/gaming-mexico-2022-videojuegos-siguen-creciendo-infografia-498x280-778742417.gif"
          alt=""
        ></img>
        <label className={label}>Nombre:</label>
        <input className={input} value={formData.name} onChange={handleInputChange} name="name" type="text" />
        <p></p>
        <label className={label}>Imagen:</label>
        <input className={input} value={formData.background_image} onChange={handleInputChange} name="background_image" type="text" />
        <p></p>
        <label className={label}>Descripción:</label>
        <p></p>
        <input className={input} value={formData.slug} onChange={handleInputChange} name="slug" type="text" />
        <label className={label}>Plataformas:</label>
        <div className={checkbox}>
        {platforms.map((platform) => (
          <div className={genreList}>
            <input type="checkbox" value={formData.platforms} name="platforms" onClick={(event) => handleGenre(event)} />
            <label className={genreList} htmlFor={platform}>{platform}</label>
        </div>))}
        </div>
        <label className={label}>Fecha de Lanzamiento:</label>
        <input type="date" value={formData.released} name="released" onChange={handleInputChange} className={date} />
        <p></p>
        <label className={label}>Rating:</label>
        <input className={input} value={formData.rating} onChange={handleInputChange} name="rating" type="text" />
        <p></p>
        <label className={label}>Género/s:</label>
        <div className={checkbox}>
        {genres.map((genre) => (
          <div className={genreList}>
            <input type="checkbox" value={formData.genres} name="genres" onClick={(event) => handleGenre(event)} />
            <label className={genreList} htmlFor={genre.name}>{genre.name}</label>
        </div>))}
        </div>
        <p></p>
        <button className={submit} type="submit">
          Crear
        </button>
      </form>
    </div>
  );
}
