import { useDispatch, useSelector } from "react-redux";
import styles from "./form.module.css";
import { useState, useEffect } from "react";
import { loadGenres, createVideogame } from "../../redux/actions/actions";
import { useNavigate } from 'react-router-dom';

export default function Form() {
  const genres = useSelector((state) => state.allGenres);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { divPrinc, img, input, label, submit, checkbox, genreList, date } = styles;

  const [formData, setFormData] = useState({
    name: "",
    image: "",
    slug: "",
    platforms: "",
    released: "",
    rating: 0,
    genres: [],
  });

  useEffect(() => {
    dispatch(loadGenres()); 
  }, [dispatch]);

  const handleGenre = (event) => {
    if (event.target.checked) {
      setFormData({ ...formData, genres: [...formData.genres, event.target.value] });
    } else {
      setFormData({ ...formData, genres: formData.genres.filter((gen) => gen.name !== event.target.value)});
    }   
};

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(createVideogame(formData));
    navigate('/home');
  };

  return (
    <div className={divPrinc}>
      <form onSubmit={handleSubmit}>
        <img
          className={img}
          src="https://pandaancha.mx/plds/articulos/froala/gaming-mexico-2022-videojuegos-siguen-creciendo-infografia-498x280-778742417.gif"
          alt=""
        ></img>
        <label className={label}>Nombre:</label>
        <input className={input} value={formData.name} name="name" type="text" />
        <label className={label}>Imagen:</label>
        <input className={input} value={formData.minWeight} name="minWeight" type="text" />
        <label className={label}>Descripci√≥n:</label>
        <input className={input} value={formData.minHeight} name="minHeight" type="text" />
        <label className={label}>Plataformas:</label>
        <input className={input} value={formData.lifespan} name="lifespan" type="text" />
        <label className={label}>Fecha de Lanzamiento:</label>
        <input type="date" className={date} />
        <label className={label}>Rating:</label>
        <input className={input} value={formData.image} name="image" type="text" />
        <label className={label}>G√©nero/s:</label>
        <div className={checkbox}>
        {genres.map((genre) => (
          <div className={genreList}>
            <input type="checkbox" value={genre.name} id={genre.name} onClick={(event) => handleGenre(event)} />
            <label className={genreList} htmlFor={genre.name}>{genre.name}</label>
        </div>))}
        </div>
        <p></p>
        <button className={submit} type="submit">
          Crear
        </button>
      </form>
    </div>
  );
}
