import { useSelector } from "react-redux";
import styles from "./form.module.css";
import { useState } from "react";

export default function Form() {
  const genres = useSelector((state) => state.allGenres);
  const { divPrinc, img, input, label, submit, inputDif } = styles;
  const [formData, setFormData] = useState({
    name: "",
    image: "",
    slug: "",
    platforms: "",
    released: "",
    rating: "",
    genres: [],
    newGenre: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleGenreAdd = () => {
    if (formData.newGenre.trim() !== "") {
      setFormData({
        ...formData,
        genres: [...formData.genres, formData.newGenre],
        newGenre: "",
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className={divPrinc}>
      <form onSubmit={handleSubmit}>
        <img
          className={img}
          src="https://services.meteored.com/img/article/el-tamano-importa-como-el-tamano-de-tu-perro-influye-en-sus-patrones-de-envejecimiento-1698077723860_768.jpg"
          alt=""
        ></img>
        <label className={label}>Nombre:</label>
        <input
          className={input}
          value={formData.name}
          onChange={handleInputChange}
          name="name"
          type="text"
        />
        <label className={label}>Imagen:</label>
        <input
          className={input}
          value={formData.minWeight}
          onChange={handleInputChange}
          name="minWeight"
          type="text"
        />
        <label className={label}>Descripci√≥n:</label>
        <input
          className={input}
          value={formData.minHeight}
          onChange={handleInputChange}
          name="minHeight"
          type="text"
        />
        <label className={label}>Plataformas:</label>
        <input
          className={input}
          value={formData.lifespan}
          onChange={handleInputChange}
          name="lifespan"
          type="text"
        />
        <label className={label}>Fecha de Lanzamiento:</label>
        <input
          className={input}
          value={formData.temperaments}
          onChange={handleInputChange}
          name="temperaments"
          type="text"
        />
        <label className={label}>Rating:</label>
        <input
          className={input}
          value={formData.image}
          onChange={handleInputChange}
          name="image"
          type="text"
        />
        <label className={label}>G√©nero/s:</label>
        {genres.map((genre) => (
          <option value={genre.name}>{genre.name}</option>
        ))}
        <button type="button" onClick={handleGenreAdd}>
          Agregar G√©nero
        </button>
        <p></p>
        <button className={submit} type="submit">
          Crear
        </button>
      </form>
    </div>
  );
}
