import './App.css';
import Landing from "./components/Landing/Landing";
import Cards from './components/Cards/Cards';
import Detail from './components/Detail/Detail';
import Nav from './components/Nav/Nav';
import { Routes, Route } from 'react-router-dom';
import { useState, useEffect } from "react";
import axios from "axios";

function App() {

  const [videogames, setVideogames] = useState([]);

  useEffect(() => {
    console.log("Updated videogames state:", videogames);
  }, [videogames]);

  const onSearch = async (name) => {
    try {
      const response = await axios.get(`http://localhost:3001/videogames/name?name=${name.toLowerCase()}`);
      console.log(response.data);
      if (response.data.length > 0) {
        setVideogames(response.data);
      } else {
        setVideogames([]); 
        window.alert("No existe el videojuego indicado");
      }
    } catch (error) {
      window.alert("Ocurrió un error al buscar el videojuego");
    }
  };

  return (
      <div className="App">
      <Nav onSearch={onSearch} />
        <Routes>
          <Route path="/" element={<Landing />}></Route>
          <Route path="/home" element={<Cards />}></Route>
          <Route path="/detail/:id" element={<Detail />}></Route>
        </Routes>
      </div>
    );
}

export default App;
