import { FILTER_BY_ORIGIN, FILTER_BY_GENRE, ORDER_BY_NAME, ORDER_BY_RATING , LOAD_VIDEOGAMES, LOAD_GENRES } from "./actions/types";

const initialState = {
  allVideogames: [],
  allGenres: [],
  videogamesOriginal: []
};

const rootReducer = (state = initialState, { type, payload }) => {
  switch (type) {

    case LOAD_VIDEOGAMES: {
      return {
        ...state,
        allVideogames: payload,
        videogamesOriginal: payload,
      };
    }
    case LOAD_GENRES: {
      return {
        ...state,
        allGenres: payload,
      };
    }

    case ORDER_BY_NAME:
      const orderByName = [...state.videogamesOriginal].sort((a, b) => {
        return payload === "A" ? a.name.localeCompare(b.name) : b.name.localeCompare(a.name);
      });
      return {
        ...state,
        allVideogames: orderByName,
      };

    case ORDER_BY_RATING:
      const orderByRating = [...state.videogamesOriginal].sort((a, b) => {
        return payload === "A" ? a.rating - b.rating : b.rating - a.rating;
      });
      return {
        ...state,
        allVideogames: orderByRating,
      };

      case FILTER_BY_GENRE: {
        const { genre } = payload;
        const filteredVideogames = state.videogamesOriginal.filter((videogame) => {
          const genresArray = videogame.genres.split(", ");
          return genresArray.some((g) => g.toLowerCase().includes(genre.toLowerCase()));
        });
      
        return {
          ...state,
          allVideogames: filteredVideogames,
        };
      }


default:
  return {
    ...state,
  };
}
};

export default rootReducer;
