import Card from "../Card/Card";
import Pagination from "../Pagination/Pagination";
import Loading from "../Loading/Loading";
import styles from "./cards.module.css";
import { loadVideogames } from "../../redux/actions/actions";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux"; 

const Cards = () => {
  const { divFondo } = styles;
  const dispatch = useDispatch();
  const videogames = useSelector((state) => state.allVideogames);
  const [pagina, setPagina] = useState(1);
  const [porPagina, setPorPagina] = useState(15);
  const [loading, setLoading] = useState(true);
  
  useEffect(() => {
    dispatch(loadVideogames()).then(() => {
      setLoading(false);
    });
  }, [dispatch]);


  const maximo = Math.ceil(videogames.length / porPagina);

  return (
    <div>
       {loading ? (
        <Loading />
      ) : (
      <div className={divFondo}>
        {videogames && videogames.slice((pagina-1)*porPagina, (pagina-1)*porPagina+porPagina).map(({ id, name, background_image, genres }, index) => {

          return (
            <Card
              key={id}
              name={name}
              image={background_image}
              genres={genres}
            />
          );
        })}
      </div>
     )}
    {!loading && <Pagination pagina={pagina} setPagina={setPagina} maximo={maximo} />}
    </div>
  );
};

export default Cards;
