import { useDispatch, useSelector } from "react-redux";
import styles from "./form.module.css";
import { useState, useEffect } from "react";
import { loadGenres } from "../../redux/actions/actions";

export default function Form() {
  const genres = useSelector((state) => state.allGenres);
  const dispatch = useDispatch();
  const { divPrinc, img, input, label, submit, inputDif } = styles;
  const [formData, setFormData] = useState({
    name: "",
    image: "",
    slug: "",
    platforms: "",
    released: "",
    rating: "",
    genres: [],
    selectedGenres: [{ id: 1, value: "" }]
  });

  useEffect(() => {
    dispatch(loadGenres()); 
  }, [dispatch]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleGenreAdd = () => {
    const newSelection = { id: formData.selectedGenres.length + 1, value: "" };
    setFormData({
      ...formData,
      selectedGenres: [...formData.selectedGenres, newSelection],
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className={divPrinc}>
      <form onSubmit={handleSubmit}>
        <img
          className={img}
          src="https://pandaancha.mx/plds/articulos/froala/gaming-mexico-2022-videojuegos-siguen-creciendo-infografia-498x280-778742417.gif"
          alt=""
        ></img>
        <label className={label}>Nombre:</label>
        <input
          className={input}
          value={formData.name}
          onChange={handleInputChange}
          name="name"
          type="text"
        />
        <label className={label}>Imagen:</label>
        <input
          className={input}
          value={formData.minWeight}
          onChange={handleInputChange}
          name="minWeight"
          type="text"
        />
        <label className={label}>Descripción:</label>
        <input
          className={input}
          value={formData.minHeight}
          onChange={handleInputChange}
          name="minHeight"
          type="text"
        />
        <label className={label}>Plataformas:</label>
        <input
          className={input}
          value={formData.lifespan}
          onChange={handleInputChange}
          name="lifespan"
          type="text"
        />
        <label className={label}>Fecha de Lanzamiento:</label>
        <input
          className={input}
          value={formData.temperaments}
          onChange={handleInputChange}
          name="temperaments"
          type="text"
        />
        <label className={label}>Rating:</label>
        <input
          className={input}
          value={formData.image}
          onChange={handleInputChange}
          name="image"
          type="text"
        />
        <label className={label}>Género/s:</label>
        <select className={input}>
        <option defaultValue>Select Genre</option>
        {genres.map((genre) => (
          <option value={genre.name}>{genre.name}</option>
        ))}
        </select>
        <button type="button" onClick={handleGenreAdd}>
          Agregar Género
        </button>
        <p></p>
        <button className={submit} type="submit">
          Crear
        </button>
      </form>
    </div>
  );
}
