import { FILTER, ORDER, LOAD_VIDEOGAMES, LOAD_GENRES } from "./actions/types";

const initialState = {
  allVideogames: [],
  allGenres: [],
  currentPage: 1,
  dogsPerPage: 8, 
};

const rootReducer = (state = initialState, { type, payload }) => {
  switch (type) {

    case LOAD_VIDEOGAMES: {
      return {
        ...state,
        allVideogames: payload,
      };
    }
    case LOAD_GENRES: {
      return {
        ...state,
        allGenres: payload,
      };
    }

    case FILTER: {
      const filtroTLista = state.allVideogames.filter(
        (videogame) => videogame.genre === payload
      );
      return {
        ...state,
        allVideogames: filtroTLista,
      };
    }


    case ORDER: 
      let videogame = state.allVideogames;
      let order = videogame.sort((a, b) => {
        if (payload === "A") {
          return a.id - b.id;
        } else if (payload === "D") {
          return b.id - a.id;
        } else {
          return 0;
        }
      });
      return {
        ...state,
        allVideogames: order,
      };

    default:
      return {
        ...state,
      };
  }
};

export default rootReducer;
