import './App.css';
import Landing from "./components/Landing/Landing";
import Cards from './components/Cards/Cards';
import Nav from './components/Nav/Nav';
import { Routes, Route } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

function App() {

  const [videogames, setVideogames] = useState([]);

  useEffect(() => {
    console.log("Updated videogames state:", videogames);
  }, [videogames]);

  const onSearch = async (name) => {
    try {
      const response = await axios.get(`https://api.rawg.io/api/games?search=${name.toLowerCase()}&key=${process.env.API_KEY}`);
      if (response.data.length > 0) {
        setVideogames(response.data);
      } else {
        setVideogames([]); 
        window.alert("No existe la raza de perros indicada");
      }
    } catch (error) {
      window.alert("Ocurrió un error al buscar la raza de perro");
    }
  };

  return (
      <div className="App">
        <Nav onSearch={onSearch} />
        <Routes>
          <Route path="/" element={<Landing />}></Route>
          <Route path="/home" element={<Cards videogames={videogames} />}></Route>
        </Routes>
      </div>
    );
}

export default App;
