import { useDispatch, useSelector } from "react-redux";
import styles from "./form.module.css";
import { useState, useEffect } from "react";
import { loadGenres, createVideogame } from "../../redux/actions/actions";
import { useNavigate } from 'react-router-dom';

export default function Form() {
  const genres = useSelector((state) => state.allGenres);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { divPrinc, img, input, label, submit, checkbox, genreList } = styles;

  const [formData, setFormData] = useState({
    name: "",
    image: "",
    slug: "",
    platforms: "",
    released: "",
    rating: "",
    genres: [],
  });

  useEffect(() => {
    dispatch(loadGenres()); 
  }, [dispatch]);


  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(createVideogame(form));
    navigate('/home');
  };

  return (
    <div className={divPrinc}>
      <form onSubmit={handleSubmit}>
        <img
          className={img}
          src="https://pandaancha.mx/plds/articulos/froala/gaming-mexico-2022-videojuegos-siguen-creciendo-infografia-498x280-778742417.gif"
          alt=""
        ></img>
        <label className={label}>Nombre:</label>
        <input
          className={input}
          value={formData.name}
          name="name"
          type="text"
        />
        <label className={label}>Imagen:</label>
        <input
          className={input}
          value={formData.minWeight}
          name="minWeight"
          type="text"
        />
        <label className={label}>Descripción:</label>
        <input
          className={input}
          value={formData.minHeight}
          name="minHeight"
          type="text"
        />
        <label className={label}>Plataformas:</label>
        <input
          className={input}
          value={formData.lifespan}
          name="lifespan"
          type="text"
        />
        <label className={label}>Fecha de Lanzamiento:</label>
        <input
          className={input}
          value={formData.temperaments}
          name="temperaments"
          type="text"
        />
        <label className={label}>Rating:</label>
        <input
          className={input}
          value={formData.image}
          name="image"
          type="text"
        />
        <label className={label}>Género/s:</label>
        <div className={checkbox}>
        {genres.map((genre) => (
          <div className={genreList}>
            <input type="checkbox" value={genre.name} id={genre.name} />
            <label className={genreList} htmlFor={genre.name}>{genre.name}</label>
        </div>))}
        </div>
        <p></p>
        <button className={submit} type="submit">
          Crear
        </button>
      </form>
    </div>
  );
}
